import React from 'react'
import styled, { keyframes } from 'styled-components'

const Section = styled.section`
width: 100vw;
height: 100vh;
position: relative;

display: flex;
justify-content: center;
align-items: center;
`

const TextContainer = styled.div`
width: 100vw;
height: 100vh;

display: flex;
flex-direction: column;
justify-content: center;
align-items: center;

background-color: var(--blue);
color: var(--white);

`

const moveUp = keyframes`
100%{
  transform: translateY(0);
}
`

const Text = styled.p`
width: 50%;
font-size: var(--fontlg);
position: relative;
height: var(--fontmd);
overflow: hidden;

span{
  position: absolute;
  transform: translateY(3rem);
  animation-name: ${moveUp};
  animation-duration: 2.5s;
  animation-timing-function: ease;
  animation-fill-mode: forwards;
  font-family: var(--fontL);
  background-image: linear-gradient(-45deg, var(--gradient));
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}
`

const Section1 = () => {
  return (
    <Section>
      <TextContainer>
        <Text> <span>"ServeU and facilities management solutions, to supply everything</span> </Text>
        <Text> <span>you need for a safe, clean and productive working environment.</span> </Text>
        <Text> <span>ServeU prides itself in understanding a complex maintenance</span> </Text>
        <Text> <span>industry. From our extensive hard and soft services to cleaning</span> </Text>
        <Text> <span>services, we are your one source for comprehensive facility</span> </Text>
        <Text> <span>management services and cost savings.‚Äù</span> </Text>
      </TextContainer>
    </Section>
  )
}

export default Section1
